import React, { useState } from "react";
import styled from "styled-components";
import LinkedContents from "./imageSliderContents/LinkedContents";

const ImageSliderContainer = styled.section`
  height: 60vh;
  width: 100vw;
  position: relative;
  /* overflow: hidden; */
  display: flex;
  img {
    width: 100%;
  }

  /* transform: translateX(
    ${(props) => (props.first ? "0" : props.second ? "100%" : "200%")}
  ); */

  /* 임시 */
  overflow: scroll;
  /* transform: ${(props) =>
    props.first ? "translateX(0)" : "translateX(100%)"}; */
`;

const ImageSlider = () => {
  const [attr, setAttr] = useState("first");

  const changeAttr = () => {
    attr === "first"
      ? setAttr("second")
      : attr === "second"
      ? setAttr("third")
      : setAttr("first");

    console.log(attr);
  };

  return (
    <>
      <ImageSliderContainer onClick={changeAttr}>
        <LinkedContents />
      </ImageSliderContainer>
    </>
  );
};

export default ImageSlider;
