import React, { useState } from "react";
import styled from "styled-components";
import ContentsBox1 from "./imageSliderContents/ContentsBox1";
import ContentsBox2 from "./imageSliderContents/ContentsBox2";
import ContentsBox3 from "./imageSliderContents/ContentsBox3";

const ImageSliderContainer = styled.section`
  height: 60vh;
  width: 100vw;
  position: relative;
  /* overflow: hidden; */
  display: flex;
  img {
    width: 100%;
  }
  transform: translateX(${(props) => (props.first ? "0" : "100%")});

  /* 임시 */
  overflow: scroll;
  /* transform: ${(props) =>
    props.first ? "translateX(0)" : "translateX(100%)"}; */
`;

const ImageSlider = () => {
  const [attr, setAttr] = useState("first");

  const changeAttr = () => {
    attr === "first"
      ? setAttr("second")
      : attr === "second"
      ? setAttr("third")
      : setAttr("first");
  };

  return (
    <>
      <ImageSliderContainer onClick={changeAttr}>
        <ContentsBox1 />
        <ContentsBox2 />
        <ContentsBox3 />
      </ImageSliderContainer>
    </>
  );
};

export default ImageSlider;
