import React, { useState } from "react";
import styled from "styled-components";
import axios from "axios";
import Button from "../Button";
import Form from "./Form";
import Label from "./Label";

const ItemContainer = styled.div`
  width: 40vw;
  display: flex;
  justify-content: flex-start;
  align-items: center;

  ::after {
    margin-left: calc(var(--margin-default) / 4);
    font-size: var(--font-size-small);
    color: var(--color-pink);
    position: relative;
  }
`;

const IDContainer = styled(ItemContainer)`
  ::after {
    content: "아이디 중복 여부를 확인해 주세요!";
    margin-left: 0;
  }
`;
const PWDContainer = styled(ItemContainer)`
  ::after {
    content: "비밀번호를 입력해 주세요!";
  }
`;
const PWDCheckContainer = styled(ItemContainer)`
  ::after {
    content: "비밀번호 일치 여부를 확인해 주세요!";
  }
`;
const NameContainer = styled(ItemContainer)`
  ::after {
    content: "이름을 입력해 주세요!";
  }
`;
const PersonalNumContainer = styled(ItemContainer)`
  ::after {
    content: "주민등록번호를 입력해 주세요!";
  }
`;
const PhoneNumContainer = styled(ItemContainer)`
  ::after {
    content: "연락처를 입력해 주세요!";
  }
`;

const InputContainer = styled.input`
  width: 8vw;
  padding: calc(var(--padding-default) / 4) 0;
  font-size: var(--font-size-normal);
  position: relative;
  border: none;
  border-bottom: 2px solid var(--font-color-default);
  text-align: center;
  &:focus {
    outline: none;
  }
`;

const PersonalNumberContainer = styled(InputContainer)`
  width: calc(6vw - var(--margin-default) / 4 - var(--font-size-large));
`;

const PhoneNumberContainer = styled(InputContainer)`
  width: calc(4vw - var(--margin-default) / 4 - var(--font-size-large));
`;

const Hyphen = styled.p`
  margin: 0 calc(var(--margin-default) / 4);
  font-size: var(--font-size-large);
  color: var(--font-color-default);
`;

const OptionsContainer = styled.div`
  width: 100%;
  height: auto;
  border: 2px solid var(--color-brown);
  border-radius: 4px;
`;

const IndividualForm = () => {
  const [data, setData] = useState([]);

  const handleSubmit = (userInfo) => {
    console.log(userInfo);
    let formData = new FormData();
    formData.append("individualData", data[0]);
    console.log(formData);
    axios
      .post("http://localhost:9090/wherewego/registertest", formData, {
        headers: { "Content-Type": `multipart/form-data` },
      })
      .then((response) => {
        console.log("response : ", JSON.stringify(response, null, 2));
      })
      .catch((error) => {
        console.log("failed", error);
      });
    console.log(userInfo);
    setData(userInfo);
  };

  return (
    <Form
      id="individual_form"
      method="post"
      encType="multipart/form-data"
      onSubmit={handleSubmit}
    >
      <IDContainer>
        <Label>아이디</Label>
        <InputContainer
          type="text"
          minLength="8"
          maxlength="20"
          name="user_id"
          id="user_id"
        />
        <Button>중복 확인</Button>
      </IDContainer>
      <PWDContainer>
        <Label>비밀번호</Label>
        <InputContainer
          type="password"
          minLength="8"
          maxlength="20"
          name="user_pwd"
          id="user_pwd"
          required
        />
      </PWDContainer>
      <PWDCheckContainer>
        <Label>비밀번호 확인</Label>
        <InputContainer
          type="password"
          minLength="8"
          maxlength="20"
          name="user_pwdcheck"
          id="user_pwdcheck"
          required
        />
      </PWDCheckContainer>
      <NameContainer>
        <Label>이름</Label>
        <InputContainer type="text" name="user_name" id="user_name" />
      </NameContainer>
      <PersonalNumContainer>
        <Label>주민등록번호</Label>
        <PersonalNumberContainer type="text" name="user_num1" id="user_num1" />
        <Hyphen> - </Hyphen>
        <PersonalNumberContainer
          type="password"
          name="user_num2"
          id="user_num2"
        />
      </PersonalNumContainer>
      <PhoneNumContainer>
        <Label>연락처</Label>
        <PhoneNumberContainer type="select" name="user_tel1" id="user_tel1" />
        <Hyphen> - </Hyphen>
        <PhoneNumberContainer type="text" name="user_tel2" id="user_tel2" />
        <Hyphen> - </Hyphen>
        <PhoneNumberContainer type="text" name="user_tel3" id="user_tel3" />
      </PhoneNumContainer>
      <ItemContainer>
        <Label>우편번호</Label>
        <InputContainer type="text" name="user_zipcode" id="user_zipcode" />
        <Button>우편번호 검색</Button>
      </ItemContainer>
      <ItemContainer>
        <Label>주소</Label>
        <InputContainer type="text" name="user_addr" id="user_addr" />
      </ItemContainer>
      <ItemContainer>
        <Label>상세주소</Label>
        <InputContainer
          type="text"
          name="user_addrdetail"
          id="user_addrdetail"
        />
      </ItemContainer>
      <ItemContainer>
        <Label>기념일</Label>
        <InputContainer type="date" name="user_date" id="user_date" />
      </ItemContainer>
      <ItemContainer>
        <Label>관심사</Label>
        <OptionsContainer>
          5
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
        </OptionsContainer>
      </ItemContainer>
      <input type="submit" value="제출" />
    </Form>
  );
};

export default IndividualForm;
