import React, { useState } from "react";
import styled from "styled-components";
import axios from "axios";
import Button from "../Button";
import Form from "./Form";

const ItemContainer = styled.div`
  width: 40vw;
  display: flex;
  justify-content: flex-start;
  align-items: center;
`;

const LabelContainer = styled.label`
  width: 8vw;
  font-size: var(--font-size-normal);
  margin-right: var(--margin-default);
`;

const InputContainer = styled.input`
  width: 8vw;
  padding: calc(var(--padding-default) / 4) 0;
  font-size: var(--font-size-normal);
  position: relative;
  border: none;
  border-bottom: 2px solid var(--font-color-default);
  &:focus::after {
    width: 8vw;
    position: relative;
    top: 0;
    left: 0;
    border-bottom: 12px solid var(--color-pink);
  }
  &:focus {
    outline: none;
  }
`;

const OptionsContainer = styled.div`
  width: 100%;
  height: auto;
  border: 2px solid var((--color-pink);
`;

const IndividualForm = () => {
  const [data, setData] = useState([]);

  const handleSubmit = (userInfo) => {
    console.log(userInfo);
    let formData = new FormData();
    formData.append("individualData", data[0]);
    console.log(formData);
    axios
      .post("http://localhost:9090/wherewego/registertest", formData, {
        headers: { "Content-Type": `multipart/form-data` },
      })
      .then((response) => {
        console.log("response : ", JSON.stringify(response, null, 2));
      })
      .catch((error) => {
        console.log("failed", error);
      });
    console.log(userInfo);
    setData(userInfo);
  };

  return (
    <Form
      id="individual_form"
      method="post"
      encType="multipart/form-data"
      onSubmit={handleSubmit}
    >
      <ItemContainer>
        <LabelContainer>아이디</LabelContainer>
        <InputContainer type="text" name="user_id" id="user_id" />
        <Button>중복 확인</Button>
      </ItemContainer>
      <ItemContainer>
        <LabelContainer>비밀번호</LabelContainer>
        <InputContainer type="password" name="user_pwd" id="user_pwd" />
      </ItemContainer>
      <ItemContainer>
        <LabelContainer>비밀번호 확인</LabelContainer>
        <InputContainer
          type="password"
          name="user_pwdcheck"
          id="user_pwdcheck"
        />
      </ItemContainer>
      <ItemContainer>
        <LabelContainer>이름</LabelContainer>
        <InputContainer type="text" name="user_name" id="user_name" />
      </ItemContainer>
      <ItemContainer>
        <LabelContainer>주민등록번호</LabelContainer>
        <InputContainer type="text" name="user_num1" id="user_num1" />
        -
        <InputContainer type="password" name="user_num2" id="user_num2" />
      </ItemContainer>
      <ItemContainer>
        <LabelContainer>연락처</LabelContainer>
        <InputContainer type="select" name="user_tel1" id="user_tel1" />
        -
        <InputContainer type="text" name="user_tel2" id="user_tel2" />
        -
        <InputContainer type="text" name="user_tel3" id="user_tel3" />
      </ItemContainer>
      <ItemContainer>
        <LabelContainer>우편번호</LabelContainer>
        <InputContainer type="text" name="user_zipcode" id="user_zipcode" />
        <Button>우편번호 검색</Button>
      </ItemContainer>
      <ItemContainer>
        <LabelContainer>주소</LabelContainer>
        <InputContainer type="text" name="user_addr" id="user_addr" />
      </ItemContainer>
      <ItemContainer>
        <LabelContainer>상세주소</LabelContainer>
        <InputContainer
          type="text"
          name="user_addrdetail"
          id="user_addrdetail"
        />
      </ItemContainer>
      <ItemContainer>
        <LabelContainer>기념일</LabelContainer>
        <InputContainer type="date" name="user_date" id="user_date" />
      </ItemContainer>
      <ItemContainer>
        <LabelContainer>관심사</LabelContainer>
        <OptionsContainer>
          <InputContainer type="checkbox" name="user_option" id="user_option" />
          {/* <InputContainer type="checkbox" name="user_option" id="user_option" />
          <InputContainer type="checkbox" name="user_option" id="user_option" />
          <InputContainer type="checkbox" name="user_option" id="user_option" />
          <InputContainer type="checkbox" name="user_option" id="user_option" />
          <InputContainer type="checkbox" name="user_option" id="user_option" />
          <InputContainer type="checkbox" name="user_option" id="user_option" />
          <InputContainer type="checkbox" name="user_option" id="user_option" /> */}
        </OptionsContainer>
      </ItemContainer>
      <input type="submit" value="제출" />
    </Form>
  );
};

export default IndividualForm;
