import React, { useState } from "react";
import Header from "../components/header/Header";
import BodyLayout from "../components/body/BodyLayout";
import Footer from "../components/footer/Footer";
import styled from "styled-components";
import Button from "../components/body/Button";

const Classification = styled.div`
  width: 60vw;
  height: auto;
  margin: calc(var(--margin-default) * 2) 0;
  display: flex;
  justify-content: space-around;
`;

const IconContainer = styled.div`
  width: 12vw;
  padding: var(--padding-default) 0;
  display: flex;
  flex-direction: column;
  text-align: center;
  font-size: var(--font-size-title-large);
  border: 2px solid var(--font-color-default);
  border-radius: 12px;
  p {
    margin-top: calc(var(--margin-default) / 2);
    font-size: var(--font-size-title-small);
  }
  :hover {
    cursor: pointer;
  }
`;

const SignUpForm = styled.form`
  width: 60vw;
  height: 80vh;
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: center;
`;

const ItemContainer = styled.div`
  width: 40vw;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  label {
    width: 8vw;
    font-size: var(--font-size-normal);
    margin-right: var(--margin-default);
  }
  input {
    width: 8vw;
    padding: calc(var(--padding-default) / 4) 0;
    font-size: var(--font-size-normal);
    position: relative;
    border: none;
    border-bottom: 2px solid var(--font-color-default);
    :focus::after {
      width: 8vw;
      position: relative;
      top: 0;
      left: 0;
      border-bottom: 12px solid var(--color-pink);
    }
    :focus {
      outline: none;
    }
  }
`;

const Registration = () => {
  const [isIndividual, setIsIndividual] = useState(true);
  const selectIndividual = () => {
    setIsIndividual(true);
  };
  const selectCompany = () => {
    setIsIndividual(false);
  };

  return (
    <>
      <Header />
      <BodyLayout>
        <Classification>
          <IconContainer onClick={selectIndividual}>
            <i className="fas fa-user-alt"></i>
            <p>개인회원</p>
          </IconContainer>
          <IconContainer onClick={selectCompany}>
            <i className="fas fa-building"></i>
            <p>법인회원</p>
          </IconContainer>
        </Classification>
        <SignUpForm>
          <ItemContainer>
            <label>아이디</label>
            <input type="text" name="username" id="username" />
            <Button>중복 확인</Button>
          </ItemContainer>
          <ItemContainer>
            <label>비밀번호</label>
            <input type="password" name="username" id="username" />
          </ItemContainer>
          <ItemContainer>
            <label>비밀번호 확인</label>
            <input type="password" name="username" id="username" />
          </ItemContainer>
          <ItemContainer>
            <label>이름</label>
            <input type="text" name="username" id="username" />
          </ItemContainer>
          <ItemContainer>
            <label>주민등록번호</label>
            <input type="text" name="username" id="username" />
            -
            <input type="password" name="username" id="username" />
          </ItemContainer>
          <ItemContainer>
            <label>연락처</label>
            <input type="select" name="username" id="username" />
            -
            <input type="text" name="username" id="username" />
            -
            <input type="text" name="username" id="username" />
          </ItemContainer>
          <ItemContainer>
            <label>우편번호</label>
            <input type="text" name="username" id="username" />
            <Button>우편번호 검색</Button>
          </ItemContainer>
          <ItemContainer>
            <label>주소</label>
            <input type="text" name="username" id="username" />
          </ItemContainer>
          <ItemContainer>
            <label>상세주소</label>
            <input type="text" name="username" id="username" />
          </ItemContainer>
          <ItemContainer>
            <label>기념일</label>
            <input type="date" name="username" id="username" />
          </ItemContainer>
          <ItemContainer>
            <label>관심사</label>
            <input type="date" name="username" id="username" />
          </ItemContainer>
        </SignUpForm>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default Registration;
