import React, { useState } from "react";
import Header from "../components/header/Header";
import BodyLayout from "../components/body/BodyLayout";
import Footer from "../components/footer/Footer";
import styled from "styled-components";
import Button from "../components/body/Button";

const Classification = styled.div`
  width: 60vw;
  height: auto;
  margin: calc(var(--margin-default) * 2) 0;
  display: flex;
  justify-content: space-around;
`;

const IconContainer = styled.div`
  width: 12vw;
  padding: var(--padding-default) 0;
  display: flex;
  flex-direction: column;
  text-align: center;
  font-size: var(--font-size-title-large);
  border: 2px solid var(--font-color-default);
  border-radius: 12px;
  p {
    margin-top: calc(var(--margin-default) / 2);
    font-size: var(--font-size-title-small);
  }
  :hover {
    cursor: pointer;
  }
`;

const SignUpForm = styled.form`
  width: 60vw;
  height: 80vh;
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: center;
`;

const ItemContainer = styled.div`
  width: 48vw;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  label {
    font-size: var(--font-size-normal);
  }
  input {
    font-size: var(--font-size-normal);
    margin: 0 var(--margin-default);
    position: relative;
    border: none;
    border-bottom: 1px solid var(--font-color-default);
    :focus::after {
      position: relative;
      border-bottom: 4px solid var(--color-pink);
    }
  }
`;

const Registration = () => {
  const [isIndividual, setIsIndividual] = useState(true);
  const selectIndividual = () => {
    setIsIndividual(true);
  };
  const selectCompany = () => {
    setIsIndividual(false);
  };

  return (
    <>
      <Header />
      <BodyLayout>
        <Classification>
          <IconContainer onClick={selectIndividual}>
            <i className="fas fa-user-alt"></i>
            <p>개인회원</p>
          </IconContainer>
          <IconContainer onClick={selectCompany}>
            <i className="fas fa-building"></i>
            <p>법인회원</p>
          </IconContainer>
        </Classification>
        <SignUpForm>
          <ItemContainer>
            <label>아이디</label>
            <input type="text" name="username" id="username" />
            <Button>중복 확인</Button>
          </ItemContainer>
          <ItemContainer>
            <label>비밀번호</label>
            <input type="password" name="username" id="username" />
          </ItemContainer>
          <ItemContainer>
            <label>비밀번호 확인</label>
            <input type="password" name="username" id="username" />
          </ItemContainer>
        </SignUpForm>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default Registration;
