import React from "react";
import styled from "styled-components";
import ContentsBox1 from "./imageSliderContents/ContentsBox1";
import ContentsBox2 from "./imageSliderContents/ContentsBox2";
import ContentsBox3 from "./imageSliderContents/ContentsBox3";

const ImageSliderContainer = styled.section`
  height: 60vh;
  width: 100vw;
  position: relative;
  /* overflow: hidden; */
  display: flex;
  img {
    width: 100%;
  }
  transform: ${(props) => (props.first ? translateX(0) : translateX(100))}
  /* 임시 */
  overflow: scroll;
`;

const ImageSlider = () => {
  return (
    <ImageSliderContainer>
      <ContentsBox1 first />
      <ContentsBox2 />
      <ContentsBox3 />
      <ContentsBox1 />
    </ImageSliderContainer>
  );
};

export default ImageSlider;
