import React, { useState } from "react";
import styled from "styled-components";
import ImageSliderContents from "./ImageSliderContents";

const ImageSliderContainer = styled.section`
  height: 60vh;
  width: 100vw;
  padding: 0;
  position: relative;
  /* overflow: hidden; */
  display: flex;

  /* 임시 */
  overflow: scroll;

  img {
    width: 100%;
  }
`;

const ImageSlider = () => {
  const [isShown, setIsShown] = useState("1st");
  const handleSlider = () => {
    console.log(isShown);
    isShown === "1st"
      ? setIsShown("2nd")
      : isShown === "2nd"
      ? setIsShown("3rd")
      : setIsShown("1st");
  };
  return (
    <ImageSliderContainer>
      <ImageSliderContents isShown={isShown} onClick={handleSlider} />
    </ImageSliderContainer>
  );
};

export default ImageSlider;
