import React from "react";
import styled from "styled-components";
import ImageSlider from "../components/Body/ImageSlider";
import Footer from "../components/Footer/Footer";
import Header from "../components/Header/Header";

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TextInImage = styled.div`
  width: 20vw;
  position: relative;
  top: -32vh;
  left: 8vw;
  h1 {
    color: white;
    font-size: var(--font-size-big-title);
    font-weight: 800;
    margin-bottom: calc(var(--margin-default) / 2);
  }
  p {
    font-size: var(--font-size-default);
    color: white;
    line-height: 2;
  }
`;

const TextContainer = styled.section`
  width: 56vw;
  height: 32vh;
  background-color: red;
`;

const About = () => {
  return (
    <PageWrapper>
      <Header />
      <ImageSlider>
        <TextInImage>
          <h1>이것은 광고란다.</h1>
          <p>
            데이트를 잘하려면 뭘 해야 할까요? 일단 애인이 있어야 합니다. <br />{" "}
            그 다음에 필요한 것이 바로 우리 사이트임 <br />
            제발 가입해
          </p>
        </TextInImage>
      </ImageSlider>
      <TextContainer></TextContainer>
      <Footer />
    </PageWrapper>
  );
};

export default About;
