import React, { useState, useRef } from "react";
import styled from "styled-components";
import Header from "../components/Header/Header";
import BodyLayout from "../components/Body/BodyLayout";
import Footer from "../components/Footer/Footer";

const ReviewsContainer = styled.div`
  width: 80vw;
  /* width: 1000px; */
  height: 80vh;
  display: flex;
  justify-content: space-around;
  overflow-x: scroll;
  box-sizing: content-box;
`;

const ReviewItem = styled.div`
  width: 200px;
  height: 100%;
  margin: 0 var(--margin-default);

  background-color: wheat;
  font-size: 40px;
  border: 2px solid black;
`;

const Reviews = () => {
  const [isDown, setIsDown] = useState(false);
  const sliderRef = useRef();
  let startX;
  let scrollLeft;

  const startDraging = (e) => {
    setIsDown(true);
    console.log("마우스 다운!");
    console.log(e.pageX);
    console.log("슬라이드ref");
    console.log(sliderRef.current);
    startX = e.pageX - sliderRef.current.offsetLeft;
    scrollLeft = sliderRef.current.scrollLeft;
  };

  const slideReviews = () => {
    console.log("마우스 무빙!");
    setIsDown(false);
  };

  const stopDraging = (e) => {
    console.log("마우스 업!");
    if (!isDown) return;
    e.preventDefault();
    const x = e.pageX - sliderRef.current.offsetLeft;
    const walk = (x - startX) * 3;
    sliderRef.current.scrollLeft = scrollLeft - walk;
  };

  console.log(isDown);

  return (
    <>
      <Header />
      <BodyLayout>
        <ReviewsContainer
          ref={sliderRef}
          onMouseDown={startDraging}
          onMouseLeave={stopDraging}
          onMouseUp={stopDraging}
          onMouseMove={slideReviews}
        >
          <ReviewItem>1</ReviewItem>
          <ReviewItem>2</ReviewItem>
          <ReviewItem>3</ReviewItem>
          <ReviewItem>4</ReviewItem>
          <ReviewItem>5</ReviewItem>
          <ReviewItem>6</ReviewItem>
          <ReviewItem>7</ReviewItem>
          <ReviewItem>8</ReviewItem>
          <ReviewItem>9</ReviewItem>
          <ReviewItem>10</ReviewItem>
        </ReviewsContainer>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default Reviews;
