import React from "react";
import styled from "styled-components";
import ImageSlider from "../components/Body/ImageSlider";
import Header from "../components/Header/Header";

const PageWrapper = styled.div`
  position: relative;
`;

const TextInImage = styled.div`
  position: relative;
  top: -32vh;
  left: 8vw;
  h1 {
    color: white;
    font-weight: 800;
    margin-bottom: var(--font-size-default);
  }
  p {
    color: white;
  }
`;

const About = () => {
  return (
    <PageWrapper>
      <Header />
      <ImageSlider />
      <TextInImage>
        <h1>이것은 광고란다.</h1>
        <p>데이트를 잘하려면 뭘 해야 할까요? 일단 애인이 있어야 합니다.</p>
      </TextInImage>
    </PageWrapper>
  );
};

export default About;
