import React from "react";
import styled from "styled-components";

const BodyContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ImageSliderContainer = styled.section`
  height: 80vh;
  overflow: hidden;
  .temp-main {
    width: 100%;
  }
`;

const StepsContainer = styled.section`
  width: 72vw;
  height: auto;
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: center;
`;

const StepWapper = styled.article`
  width: 80%;
  height: 32vh;
  border: 1px solid black;
  margin-top: var(--margin-default);
  display: flex;
`;

const StepCircle = styled.div`
  width: 32vh;
  height: 100%;
  border-radius: 100%;
  background-color: var(--concept-color6);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const StepText = styled.div`
  width: calc(100% - 32vh);
  height: 100%;
  margin: 0 var(--margin-default);
  display: flex;
`;

const Body = () => {
  return (
    <BodyContainer>
      <ImageSliderContainer>
        <img className="temp-main" src="images/temp_main.jpg" />
      </ImageSliderContainer>
      <StepsContainer>
        <StepWapper>
          <StepCircle>STEP 1</StepCircle>
          <StepText>회원가입</StepText>
        </StepWapper>
        <StepWapper>
          <StepText>회원가입</StepText>
          <StepCircle />
        </StepWapper>
        <StepWapper>
          <StepCircle />
          <StepText>회원가입</StepText>
        </StepWapper>
        <StepWapper>
          <StepText>회원가입</StepText>
          <StepCircle />
        </StepWapper>
      </StepsContainer>
    </BodyContainer>
  );
};

export default Body;
