import React, { useState } from "react";
import styled from "styled-components";
import ContentsBox1 from "./ContentsBox1";
import ContentsBox2 from "./ContentsBox2";
import ContentsBox3 from "./ContentsBox3";

const Container = styled.div`
  width: auto;
  display: flex;

  transform: translateX(
    ${(props) => (props.first ? "0" : props.second ? "10%" : "20%")}
  );
`;

const LinkedContents = () => {
  const [attr, setAttr] = useState("first");

  const changeAttr = () => {
    attr === "first"
      ? setAttr("second")
      : attr === "second"
      ? setAttr("third")
      : setAttr("first");
    console.log(attr);
  };
  return (
    <Container onClick={changeAttr}>
      <ContentsBox1 />
      <ContentsBox2 />
      <ContentsBox3 />
    </Container>
  );
};

export default LinkedContents;
