import React from "react";
import styled from "styled-components";

const BodyContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ImageSliderContainer = styled.section`
  height: 80vh;
  overflow: hidden;
  .temp-main {
    width: 100%;
  }
`;

const StepsContainer = styled.section`
  width: 72vw;
  height: auto;
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: center;
`;

const StepWapper = styled.article`
  width: 80%;
  height: 32vh;
  margin: calc(var(--margin-default) * 2);
  display: flex;
`;

const StepCircle = styled.div`
  width: 32vh;
  height: 100%;
  border-radius: 100%;
  background-color: var(--concept-color6);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const StepTextContainer = styled.div`
  width: calc(100% - 32vh);
  height: 100%;
  margin: 0 var(--margin-default);
  padding: calc(var(--padding-default) * 3);
  display: flex;
  flex-direction: column;
`;

const Body = () => {
  return (
    <BodyContainer>
      <ImageSliderContainer>
        <img className="temp-main" src="images/temp_main.jpg" />
      </ImageSliderContainer>
      <StepsContainer>
        <StepWapper>
          <StepCircle>STEP 1</StepCircle>
          <StepTextContainer>회원가입</StepTextContainer>
        </StepWapper>
        <StepWapper>
          <StepTextContainer>플랜 짜기</StepTextContainer>
          <StepCircle>STEP 2</StepCircle>
        </StepWapper>
        <StepWapper>
          <StepCircle>STEP 3</StepCircle>
          <StepTextContainer>데이트</StepTextContainer>
        </StepWapper>
        <StepWapper>
          <StepTextContainer>후기 작성</StepTextContainer>
          <StepCircle>STEP 4</StepCircle>
        </StepWapper>
      </StepsContainer>
    </BodyContainer>
  );
};

export default Body;
