import React, { useState } from "react";
import styled from "styled-components";
import axios from "axios";
import Button from "../Button";
import Form from "./Form";
import Label from "./Label";

const ItemContainer = styled.div`
  width: 40vw;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  p {
    font-size: var(--font-size-small);
    color: var(--color-pink);
    display: flex;
    align-items: center;
    i {
      font-size: var(--font-size-normal);
    }
  }
`;

const InputContainer = styled.input`
  width: 12vw;
  padding: calc(var(--padding-default) / 4) 0;
  font-size: var(--font-size-normal);
  position: relative;
  border: none;
  border-bottom: 2px solid var(--font-color-default);
  /* &:focus::after {
    width: 8vw;
    position: relative;
    top: 0;
    left: 0;
    border-bottom: 12px solid var(--color-pink);
  } */
  &:focus {
    outline: none;
  }
`;

const PersonalNumberContainer = styled(InputContainer)`
  width: 6vw;
`;

const OptionsContainer = styled.div`
  width: 100%;
  height: auto;
  border: 2px solid var(--color-brown);
  border-radius: 4px;
`;

const IndividualForm = () => {
  const [data, setData] = useState([]);

  const handleSubmit = (userInfo) => {
    console.log(userInfo);
    let formData = new FormData();
    formData.append("individualData", data[0]);
    console.log(formData);
    axios
      .post("http://localhost:9090/wherewego/registertest", formData, {
        headers: { "Content-Type": `multipart/form-data` },
      })
      .then((response) => {
        console.log("response : ", JSON.stringify(response, null, 2));
      })
      .catch((error) => {
        console.log("failed", error);
      });
    console.log(userInfo);
    setData(userInfo);
  };

  return (
    <Form
      id="individual_form"
      method="post"
      encType="multipart/form-data"
      onSubmit={handleSubmit}
    >
      <ItemContainer>
        <Label>아이디</Label>
        <InputContainer type="text" name="user_id" id="user_id" />
        <Button>중복 확인</Button>
        <p>
          <i className="fas fa-exclamation-circle"></i>&nbsp; 아이디 중복 여부를
          확인해 주세요!
        </p>
      </ItemContainer>
      <ItemContainer>
        <Label>비밀번호</Label>
        <InputContainer type="password" name="user_pwd" id="user_pwd" />
      </ItemContainer>
      <ItemContainer>
        <Label>비밀번호 확인</Label>
        <InputContainer
          type="password"
          name="user_pwdcheck"
          id="user_pwdcheck"
        />
      </ItemContainer>
      <ItemContainer>
        <Label>이름</Label>
        <InputContainer type="text" name="user_name" id="user_name" />
      </ItemContainer>
      <ItemContainer>
        <Label>주민등록번호</Label>
        <InputContainer type="text" name="user_num1" id="user_num1" />
        -
        <InputContainer type="password" name="user_num2" id="user_num2" />
      </ItemContainer>
      <ItemContainer>
        <Label>연락처</Label>
        <InputContainer type="select" name="user_tel1" id="user_tel1" />
        -
        <InputContainer type="text" name="user_tel2" id="user_tel2" />
        -
        <InputContainer type="text" name="user_tel3" id="user_tel3" />
      </ItemContainer>
      <ItemContainer>
        <Label>우편번호</Label>
        <InputContainer type="text" name="user_zipcode" id="user_zipcode" />
        <Button>우편번호 검색</Button>
      </ItemContainer>
      <ItemContainer>
        <Label>주소</Label>
        <InputContainer type="text" name="user_addr" id="user_addr" />
      </ItemContainer>
      <ItemContainer>
        <Label>상세주소</Label>
        <InputContainer
          type="text"
          name="user_addrdetail"
          id="user_addrdetail"
        />
      </ItemContainer>
      <ItemContainer>
        <Label>기념일</Label>
        <InputContainer type="date" name="user_date" id="user_date" />
      </ItemContainer>
      <ItemContainer>
        <Label>관심사</Label>
        <OptionsContainer>
          5
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
          <InputContainer type="checkbox" />
        </OptionsContainer>
      </ItemContainer>
      <input type="submit" value="제출" />
    </Form>
  );
};

export default IndividualForm;
