import React, { Children } from "react";
import styled from "styled-components";

const ImageSliderContainer = styled.section`
  height: 60vh;
  width: 100vw;
  position: relative;
  overflow: hidden;
  .temp-image {
    width: 100%;
  }
`;

const TextInImage = styled.div`
  width: 20vw;
  position: relative;
  z-index: 2;
  top: -112vh;
  left: 6vw;
  h1 {
    color: white;
    font-size: var(--font-size-big-title);
    font-weight: 800;
    margin-bottom: calc(var(--margin-default) / 2);
  }
  p {
    font-size: var(--font-size-default);
    color: white;
    line-height: 2;
  }
`;

const ImageSlider = ({ children }) => {
  return <ImageSliderContainer>{children}</ImageSliderContainer>;
};

export default ImageSlider;
