import React, { useState } from "react";
import styled, { css } from "styled-components";
import ImageSliderContents from "./ImageSliderContents";

const ImageSliderContainer = styled.section`
  height: 60vh;
  width: 100vw;
  position: relative;
  /* overflow: hidden; */
  display: flex;

  /* 임시 */
  overflow: scroll;

  img {
    width: 100%;
    transform: ${(props) =>
      props.slided
        ? css`
            translateX(-100vw);
          `
        : css`
            translateX(0);
          `};
  }
`;

const ImageSlider = () => {
  const [slided, setSlided] = useState(false);

  const handleSlider = () => {
    setSlided(!slided);
  };

  return (
    <ImageSliderContainer onClick={handleSlider}>
      <ImageSliderContents slided />
    </ImageSliderContainer>
  );
};

export default ImageSlider;
