import React, { useState, useRef } from "react";
import styled from "styled-components";
import Header from "../components/Header/Header";
import BodyLayout from "../components/Body/BodyLayout";
import Footer from "../components/Footer/Footer";

const ReviewsContainer = styled.ul`
  width: 80vw;
  height: 80vh;
  display: flex;
  overflow-x: scroll;
  overflow-y: hidden;
  white-space: nowrap;

  user-select: none;
`;

const ReviewItem = styled.li`
  width: auto;
  height: 100%;
  /* margin: 0 var(--margin-default); */

  /* 체크용 > 삭제 */
  background-color: wheat;
  font-size: 40px;
  border: 2px solid black;
`;

const ProfileBox = styled.div`
  width: 240px;
  height: 200px;
`;

const Reviews = () => {
  const [isDown, setIsDown] = useState(false);
  const sliderRef = useRef();
  // 스크롤링은 useEffect를 사용해야 할 듯
  let startX;
  let scrollL;

  const startDraging = (e) => {
    setIsDown(true);
    console.log("마우스 다운!");
    if (isDown) {
      console.log("isDown? ", isDown);
    }
    console.log(sliderRef.current);
    startX = e.pageX - sliderRef.current.offsetLeft;
    console.log("startX", startX);
    scrollL = sliderRef.current.scrollLeft;
    console.log("scrollL", sliderRef.current.scrollLeft);
  };

  const slideReviews = (e) => {
    console.log("마우스 무빙!");
    if (!isDown) return;
    e.preventDefault();
    const x = e.pageX - sliderRef.current.offsetLeft;
    console.log("x", x);
    console.log("startX", startX);
    const walk = (x - startX) * 3;
    console.log("walk", walk);
    sliderRef.current.scrollLeft = scrollL - walk;
    console.log("slider", sliderRef.current.scrollLeft);
  };

  const stopDraging = () => {
    console.log("마우스 업!");
    setIsDown(false);
  };

  console.log(isDown);

  return (
    <>
      <Header />
      <BodyLayout>
        <ReviewsContainer
          ref={sliderRef}
          onMouseDown={startDraging}
          onMouseMove={slideReviews}
          onMouseLeave={stopDraging}
          onMouseUp={stopDraging}
        >
          <ReviewItem>
            <ProfileBox>0</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>1</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>2</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>3</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>4</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>5</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>6</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>7</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>8</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>9</ProfileBox>
          </ReviewItem>
          <ReviewItem>
            <ProfileBox>10</ProfileBox>
          </ReviewItem>
        </ReviewsContainer>
      </BodyLayout>
      <Footer />
    </>
  );
};

export default Reviews;
