import React from "react";
import styled from "styled-components";
import Button from "../Button";
import Form from "./Form";

const ItemContainer = styled.div`
  width: 40vw;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  label {
    width: 8vw;
    font-size: var(--font-size-normal);
    margin-right: var(--margin-default);
  }
`;

const InputContainer = styled.input`
  input {
    width: 8vw;
    padding: calc(var(--padding-default) / 4) 0;
    font-size: var(--font-size-normal);
    position: relative;
    border: none;
    border-bottom: 2px solid var(--font-color-default);
    &:focus::after {
      width: 8vw;
      position: relative;
      top: 0;
      left: 0;
      border-bottom: 12px solid var(--color-pink);
    }
    &:focus {
      outline: none;
    }
  }
`;

const IndividualForm = () => {
  return (
    <Form>
      <ItemContainer>
        <label>아이디</label>
        <input type="text" name="user_id" id="user_id" />
        <Button>중복 확인</Button>
      </ItemContainer>
      <ItemContainer>
        <label>비밀번호</label>
        <input type="password" name="user_pwd" id="user_pwd" />
      </ItemContainer>
      <ItemContainer>
        <label>비밀번호 확인</label>
        <input type="password" name="user_pwdcheck" id="user_pwdcheck" />
      </ItemContainer>
      <ItemContainer>
        <label>이름</label>
        <input type="text" name="user_name" id="user_name" />
      </ItemContainer>
      <ItemContainer>
        <label>주민등록번호</label>
        <input type="text" name="user_num1" id="user_num1" />
        -
        <input type="password" name="user_num2" id="user_num2" />
      </ItemContainer>
      <ItemContainer>
        <label>연락처</label>
        <input type="select" name="user_tel1" id="user_tel1" />
        -
        <input type="text" name="user_tel2" id="user_tel2" />
        -
        <input type="text" name="user_tel3" id="user_tel3" />
      </ItemContainer>
      <ItemContainer>
        <label>우편번호</label>
        <input type="text" name="user_zipcode" id="user_zipcode" />
        <Button>우편번호 검색</Button>
      </ItemContainer>
      <ItemContainer>
        <label>주소</label>
        <input type="text" name="user_addr" id="user_addr" />
      </ItemContainer>
      <ItemContainer>
        <label>상세주소</label>
        <input type="text" name="user_addrdetail" id="user_addrdetail" />
      </ItemContainer>
      <ItemContainer>
        <label>기념일</label>
        <input type="date" name="user_date" id="user_date" />
      </ItemContainer>
      <ItemContainer>
        <label>관심사</label>
        <div>
          <input type="checkbox" name="user_option" id="user_option" />
        </div>
      </ItemContainer>
    </Form>
  );
};

export default IndividualForm;
