import React from "react";
import styled, { css } from "styled-components";
import ImageSliderContents from "./ImageSliderContents";

const ImageSliderContainer = styled.section`
  height: 60vh;
  width: 100vw;
  position: relative;
  /* overflow: hidden; */
  display: flex;

  /* 임시 */
  overflow: scroll;

  img {
    width: 100%;
    ${(props) => (props.slided ? "translateX(-100vw)" : "translateX(0)")};
  }
`;

const ImageSlider = () => {
  const handleSlider = () => {};

  return (
    <ImageSliderContainer onClick={handleSlider}>
      <ImageSliderContents />
    </ImageSliderContainer>
  );
};

export default ImageSlider;
