import React, { useState } from "react";
import styled from "styled-components";
import ContentsBox1 from "./ContentsBox1";
import ContentsBox2 from "./ContentsBox2";
import ContentsBox3 from "./ContentsBox3";

const Container = styled.div`
  width: auto;
  display: flex;
  transition-duration: 0.5s;

  transform: translateX(
    ${(props) => {
      console.log(props.idx);
      return props.idx === "first"
        ? "0"
        : props.idx === "second"
        ? "-100%"
        : "-200%";
    }}
  );
`;

const LinkedContents = () => {
  const [idx, setIdx] = useState("first");

  const changeIdx = () => {
    idx === "first"
      ? setIdx("second")
      : idx === "second"
      ? setIdx("third")
      : setIdx("first");
    console.log(idx);
  };
  return (
    <Container onClick={changeIdx} idx={idx}>
      <ContentsBox1 />
      <ContentsBox2 />
      <ContentsBox3 />
    </Container>
  );
};

export default LinkedContents;
